<?phpnamespace Bfg\SpeedTest;/** * Class Meter * @package Bfg\SpeedTest */class Meter{    /**     * Number of launches     *     * @var int     */    public int $times = 1;    /**     * Start microtime     *     * @var int     */    public int $start = 0;    /**     * End microtime     *     * @var int     */    public int $end = 0;    /**     * Diff (End-Start) converted to seconds     *     * @var float     */    public float $diff = 0;    /**     * On one time (Diff\Times) seconds     *     * @var float     */    public float $on_one_time = 0;    /**     * Data for call tik test     *     * @var \Closure|null     */    public \Closure|null $call_tik = null;    /**     * Meter constructor.     * @param  array  $props     */    public function __construct(array $props)    {        $this->set($props);    }    /**     * Set props to class     *     * @param  array  $props     * @return $this     */    public function set(array $props): static    {        foreach ($props as $key => $prop) {            $this->{$key} = $prop;        }        return $this;    }    /**     * Call data for meter     * @param  callable  $call     * @return $this     */    public function start(callable $call): static    {        $this->start = hrtime(1);        for ($i = 1; $i <= $this->times; $i++) {            call_user_func($call);            $f1 = hrtime(1);            if ($this->call_tik) call_user_func($this->call_tik);            $this->start -= (hrtime(1)-$f1);        }        $this->end = hrtime(1);        $this->diff = (($this->end - $this->start) / 1e+6) / 1000;        $this->on_one_time = $this->diff / $this->times;        return $this;    }    /**     * @param  array  $props     * @return static     */    public static function create(array $props): static    {        return new static($props);    }}